(67, 1, '2018-08-15 03:43:50', '2018-08-15 03:43:50', '<div class=\"row\">\r\n<div class=\"col s10\">\r\n<div id=\"post-29\" class=\"card\">\r\n<div class=\"card-content\">\r\n\r\n<!--titulo receta-->\r\n<div class=\"card green darken-4\">\r\n<div class=\"card-content\">\r\n<h5 class=\"entry-title white-text\">Camarones</h5>\r\n</div>\r\n</div>\r\n<!-- .entry-header -->\r\n\r\n<!--imagen receta-->\r\n<div class=\"card\">\r\n<div class=\"center\"><!-- .entry-image --></div>\r\n<!--partes receta-->\r\n<div class=\"card-content\">\r\n\r\n<!--chips-->\r\n<div class=\"chip entry-footer\"><i class=\"material-icons left\">account_circle</i>\r\n\r\n</div>\r\n<!-- .entry-footer -->\r\n\r\n<!--descripción-->\r\n<div class=\"row\">\r\n<div class=\"col s6\">\r\n<div class=\"card\">\r\n<div class=\"card-content\">\r\n<div class=\"card blue-grey darken-4\">\r\n<div class=\"card-content\">\r\n<h4 class=\"white-text\"><i class=\"material-icons left\">reorder</i>Listado de recetas por categoría platos</h4>\r\n</div>\r\n</div>\r\n<ul>\r\n 	<li>Camarones</li>\r\n</ul>\r\n</div>\r\n</div>\r\n</div>\r\n<!--ingredientes-->\r\n<div class=\"col s6\">\r\n<div class=\"card\">\r\n<div class=\"card-content\">\r\n<div class=\"card blue darken-4\">\r\n<div class=\"card-content\">\r\n<h4 class=\"white-text\"><i class=\"material-icons left\">shopping_cart</i>Listado de recetas por categoría platos</h4>\r\n</div>\r\n</div>\r\n<ul class=\"ingredients\">\r\n 	<li id=\"ingredient_1816264\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">1 lb.</span> camarones totalmente limpios (approx. 25 por libra)</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816265\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">3 cdas</span> aceite de oliva extra-virgen</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816266\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">3 dientes</span> ajo finamente picados</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816267\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">1 cda.</span> cebolleta (chives en Ingles)</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816268\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">1/2 tz</span> vino blanco</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816269\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">4 cdas</span> mantequilla sin sal (manteca en argentino)</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816270\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">1 cda.</span> jugo de limón</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816271\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">1 cda.</span> perejil italiano picadito</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816272\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\">sal</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816273\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">6 rodajas</span> pan italiano (1/4 de pulgada de ancho)</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816274\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">1</span> limon cortado en rodajas</div>\r\n</div></li>\r\n 	<li id=\"ingredient_1816275\" class=\"ingredient \" data-record=\"/es/recipes/98202-camarones-al-ajillo/ingredients\">\r\n<div class=\"field-group--ingredient\" data-field-group=\"\">\r\n<div class=\"ingredient__details\" data-field=\"\" data-field-name=\"quantity_and_name\" data-field-hint=\"Hacer clic en Intro para añadir otro ingrediente\" data-placeholder=\"100 gr. harina\" data-maxlength=\"200\"><span class=\"ingredient__quantity\">1/4 cdta.</span> aji molido</div>\r\n</div></li>\r\n 	<li class=\"TrT0Xe\"></li>\r\n</ul>\r\n</div>\r\n</div>\r\n</div>\r\n</div>\r\n<!--preparacón-->\r\n<div class=\"row\">\r\n<div class=\"col s12\">\r\n<div class=\"card\">\r\n<div class=\"card-content\">\r\n<div class=\"card deep-purple darken-4\">\r\n<div class=\"card-content\">\r\n<h4 class=\"white-text\"><i class=\"material-icons left\">done_all</i>Listado de recetas por categoría platos</h4>\r\n<ol class=\"numbered-list ui-sortable\" style=\"box-sizing: border-box; margin: 0px; counter-reset: numList 0; list-styl